// Get the HTML content from the AI agent
const htmlContent = $input.all()[0].json.output;

// Create CSS styles that match the original resume format exactly
const cssStyles = 'body { font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif; line-height: 1.4; color: #333; max-width: 8.5in; margin: 0 auto; padding: 1in; background: white; font-size: 11pt; } ' +
'h1 { font-size: 24pt; font-weight: bold; margin: 0 0 4pt 0; color: #000; } ' +
'h2 { font-size: 14pt; font-weight: bold; margin: 20pt 0 8pt 0; color: #000; } ' +
'h3 { font-size: 11pt; font-weight: bold; margin: 12pt 0 4pt 0; color: #000; } ' +
'p { margin: 0 0 6pt 0; line-height: 1.3; } ' +
'ul { margin: 4pt 0 8pt 0; padding-left: 16pt; list-style-type: disc; } ' +
'li { margin: 2pt 0; line-height: 1.3; } ' +
'.contact-info { margin-bottom: 16pt; font-size: 10pt; line-height: 1.2; } ' +
'.contact-info p { margin: 1pt 0; } ' +
'.job-title { font-size: 12pt; font-weight: normal; margin: 2pt 0 12pt 0; color: #666; } ' +
'.job-header { font-weight: bold; margin: 8pt 0 2pt 0; } ' +
'.job-company { font-style: italic; margin: 0 0 4pt 0; font-size: 10pt; } ' +
'.section { margin-bottom: 16pt; } ' +
'.skills-section ul { list-style-type: none; padding-left: 0; } ' +
'.skills-section li { margin: 1pt 0; } ' +
'strong { font-weight: bold; } ' +
'a { color: #0066cc; text-decoration: none; } ' +
'a:hover { text-decoration: underline; } ' +
'@media print { body { padding: 0.5in; font-size: 10pt; } h1 { font-size: 22pt; } h2 { font-size: 13pt; } }';

// Build the complete HTML
const styledHtml = '<!DOCTYPE html><html><head><meta charset="UTF-8"><style>' + cssStyles + '</style></head><body>' + htmlContent + '</body></html>';

return {
  json: {
    formattedResume: styledHtml,
    status: "success"
  }
};

